mapper.select:



    and a.value between #{valueRange.min} and #{valueRange.max}


    and a.lastmod between #{timeRange.min, jdbcType=DATE} and #{timeRange.max, jdbcType=DATE}

    and a.t0 between #{dateRange.min, jdbcType=DATE} and #{dateRange.max, jdbcType=DATE}
    and extract(year from a.t0)=#{year}
    and a.t0 is null
    and a.t0 is null

    and a.op=#{opId}
    and a.cat=#{categoryId}
    and x.tag=#{tagId}
    and a.phase=#{phaseId}
    and a.nvote between #{voteCountRange.min} and #{voteCountRange.max}
    and a.nlike between #{likerCountRange.min} and #{likerCountRange.max}
    and a.nread between #{readCountRange.min} and #{readCountRange.max}
    and a.op is null
    and a.cat is null
    and x.tag is null
    and a.phase is null


order by id desc
